sys_script_include.script:
var AdvancedReffQualifier = Class.create();
AdvancedReffQualifier.prototype = {
    initialize: function() {
    },

	getADUsers: function()
	{

		var grp = current.variables.assignment_group_name.sys_id;
		var groupGr = new GlideRecord('sys_user_grmember');
		groupGr.addEncodedQuery('group', grp);
		groupGr.query();

		//var userMembers='';
		var userMembers= [];
		while(groupGr.next())
			{
				//userMembers+= ',' +groupGr.user.sys_id;
				userMembers.push(groupGr.getValue('user'));
			}
		return 'sys_idIN' +userMembers;


	},

    type: 'AdvancedReffQualifier'
};
